function h=SubplotLetter(letter,xpn,ypn,ax)%function SubplotLetter(letter,xpn,ypn,ax)%label a subplot with the letter passed to the axis passed at normalized position%xpn,ypn (0 to 1)if ~exist('xpn')	xpn=0.05;endif ~exist('ypn')	ypn=0.95;endif ~exist('ax')	ax=gca;end%xpn%ypn%ax%compute limitsxlims=get(ax,'XLim');ylims=get(ax,'YLim');xsc=get(ax,'XScale');ysc=get(ax,'YScale');if strcmp(xsc,'log')==1	xp=10^(log10(xlims(1))+(xpn)*(log10(xlims(2)) - log10(xlims(1)))); else	xp=xlims(1)+(xpn)*(xlims(2) -xlims(1)); endif strcmp(ysc,'log')==1	yp=10^(log10(ylims(1))+(ypn)*(log10(ylims(2)) - log10(ylims(1))));else	yp=ylims(1)+(ypn)*(ylims(2) -ylims(1)) ;endoldax=gca;axes(ax)h=text(xp,yp,letter);set(h,'fontweight','bold')axes(oldax)if  strcmp(computer,'PCWIN')    set(h,'fontname','Times New Roman')else    set(h,'fontname','times')end